// IMPORT ALL THE THINGS NEEDED FROM OTHER JAVASCRIPT SOURCE FILES
import React, { Component } from 'react';
import testData from './test/testData.json'
import jsTPS from './common/jsTPS.js'
import AddNewItem_Transaction from './transactions/AddNewItem_Transaction.js'


// THESE ARE OUR REACT COMPONENTS
import Navbar from './components/Navbar'
import LeftSidebar from './components/LeftSidebar'
import Workspace from './components/Workspace'
{/*import ItemsListHeaderComponent from './components/ItemsListHeaderComponent'
import ItemsListComponent from './components/ItemsListComponent'
import ListsComponent from './components/ListsComponent'
*/}
class App extends Component {
  constructor(props) {
    // ALWAYS DO THIS FIRST
    super(props);

    // DISPLAY WHERE WE ARE
    console.log("App constructor");

    // MAKE OUR TRANSACTION PROCESSING SYSTEM
    this.tps = new jsTPS();
    this.temp=3;

    // CHECK TO SEE IF THERE IS DATA IN LOCAL STORAGE FOR THIS APP
    let recentLists = localStorage.getItem("recentLists");
    console.log("recentLists: " + recentLists);
    if (!recentLists) {
      recentLists = JSON.stringify(testData.toDoLists);
      localStorage.setItem("toDoLists", recentLists);
    }
    recentLists = JSON.parse(recentLists);

    // FIND OUT WHAT THE HIGHEST ID NUMBERS ARE FOR LISTS
    let highListId = -1;
    let highListItemId = -1;
    for (let i = 0; i < recentLists.length; i++) {
      let toDoList = recentLists[i];
      if (toDoList.id > highListId) {
        highListId = toDoList.id;
      }
      for (let j = 0; j < toDoList.items.length; j++) {
        let toDoListItem = toDoList.items[j];
        if (toDoListItem.id > highListItemId)
        highListItemId = toDoListItem.id;
      }
    };

    // SETUP OUR APP STATE
    this.state = {
      toDoLists: recentLists,
      currentList: {items: []},
      nextListId: highListId+1,
      nextListItemId: highListItemId+1,
      useVerboseFeedback: true
    }
  }

  // WILL LOAD THE SELECTED LIST
  loadToDoList = (toDoList) => {
    console.log("loading " + toDoList);

    // MAKE SURE toDoList IS AT THE TOP OF THE STACK BY REMOVING THEN PREPENDING
    const nextLists = this.state.toDoLists.filter(testList =>
      testList.id !== toDoList.id
    );
    nextLists.unshift(toDoList);

    this.setState({
      toDoLists: nextLists,
      currentList: toDoList
    });
  }

  addNewList = () => {
    let newToDoListInList = [this.makeNewToDoList()];
    let newToDoListsList = [...newToDoListInList, ...this.state.toDoLists];
    let newToDoList = newToDoListInList[0];

    // AND SET THE STATE, WHICH SHOULD FORCE A render
    this.setState({
      toDoLists: newToDoListsList,
      currentList: newToDoList,
      nextListId: this.state.nextListId+1
    }, this.afterToDoListsChangeComplete);

  }

  makeNewToDoList = () => {
    let newToDoList = {
      id: this.state.nextListId,
      name: 'Untitled',
      items: []
    };
    return newToDoList;
  }
  //Transaction
  addNewItemTransaction = () =>{
    let transaction = new AddNewItem_Transaction(this);
    this.tps.addTransaction(transaction);
  }


  //Constr
  addNewItem = () =>{
    let temp=this.state.currentList;
    let newToDoListItem = [this.makeNewToDoListItem()];
    temp.items = [...temp.items, ...newToDoListItem];

    this.setState({
      currentList:temp,
      nextListItemId:this.state.nextListItemId+1
    })
    return newToDoListItem[0];
  }

  makeNewToDoListItem = () =>  {
    let newToDoListItem = {
      id: this.state.nextListItemId,
      description: "No Description",
      due_date
      :  new Date().toUTCString(),
      status: "incomplete"
    };
    return newToDoListItem;
  }

  // THIS IS A CALLBACK FUNCTION FOR AFTER AN EDIT TO A LIST
  afterToDoListsChangeComplete = () => {
    console.log("App updated currentToDoList: " + this.state.currentList);

    // WILL THIS WORK? @todo
    let toDoListsString = JSON.stringify(this.state.toDoLists);
    localStorage.setItem("recent_work", toDoListsString);
  }


  //Construction site
  moveItem = (id,step) =>{
    var items = this.state.currentList.items;
    var index = -1;
    for(var i=0;i<items.length;i++){
      if(items[i].id==id){
         index=i;
      }
    }
    if((index>0&&step==1)|(index<items.length-1&&step==-1)){
      var temp=items[index];
      items[index]=items[index-step];
      items[index-step]=temp;
    }
    this.setState({
      currentList:{items}
    })
  }

  deleteItem = (id) =>{
    var items = this.state.currentList.items;
    var index = -1;
    for(var i=0;i<items.length;i++){
      if(items[i].id==id){
        index=i;
     }
    }
    items.splice(index, 1);
    this.setState({
      currentList:{items}
    })
  }

  closeCurrentList = () =>{
    this.setState({
      currentList: {items: []}
    })
  }

  itemChange = (id,desc,date,status) =>{
    var items = this.state.currentList.items;
    for(var i=0;i<items.length;i++){
      if(items[i].id==id){
          items[i].description =desc;
          items[i].due_date    =date;
          items[i].status      =status;
      }
    }
  }

  removeCurrentList = () =>{
    var todoList=this.state.toDoLists
    var index = -1;
    for(var i=0;i<todoList.length;i++){
      if(todoList[i]===this.state.currentList){
        index=i;
      }
    }
    todoList.splice(index, 1);
    this.setState({
      toDoLists: todoList,
      currentList: {items: []}
    })
  }

  // SIMPLE UNDO/REDO FUNCTIONS
  undo = () => {
    console.log(this.temp);
    this.tps.undoTransaction();
    if (this.tps.hasTransactionToUndo()) {
        this.tps.undoTransaction();
        // if (!this.tps.hasTransactionToUndo()) {
        //     this.view.disableButton("undo-button");
        // }
        // this.view.enableButton("redo-button");
    }
  } 

  // redo =()=> {
  //   if (this.tps.hasTransactionToRedo()) {
  //       this.tps.doTransaction();
  //       // if (!this.tps.hasTransactionToRedo()) {
  //       //     this.view.disableButton("redo-button");
  //       // }
  //       // this.view.enableButton("undo-button");
  //   }
  // }


  render() {
    let items = this.state.currentList.items;
    console.log(this.state.toDoLists);
    return (
      <div id="root">
        <Navbar />
        <LeftSidebar 
          toDoLists={this.state.toDoLists}
          loadToDoListCallback={this.loadToDoList}
          addNewListCallback={this.addNewList}
        />
        <Workspace 
          toDoListItems={items} 
          moveItemCallBack={this.moveItem}
          closeCurrentListCallBack={this.closeCurrentList}
          deleteItemCallBack={this.deleteItem}
          itemChangeCallBack={this.itemChange}
          removeCurrentListCallBack={this.removeCurrentList}
          addNewItemCallBack={this.addNewItemTransaction}
          undoCallBack={this.undo}
          // redoCallBack={this.redo}
        />
      </div>
    );
  }
}

export default App;
