// IMPORT ALL THE THINGS NEEDED FROM OTHER JAVASCRIPT SOURCE FILES
import React, { Component } from 'react'
import KeyboardArrowUp from '@material-ui/icons/KeyboardArrowUp';
import KeyboardArrowDown from '@material-ui/icons/KeyboardArrowDown';
import Close from '@material-ui/icons/Close';

class ToDoItem extends Component {
    constructor(props) {
        super(props);
        this.state ={
            taskChange:false,
            dueDateChange:false,
            statusChange:false,
        }
        // DISPLAY WHERE WE ARE
        console.log("\t\t\tToDoItem " + this.props.toDoListItem.id + " constructor");
    }

    componentDidMount = () => {
        // DISPLAY WHERE WE ARE
        console.log("\t\t\tToDoItem " + this.props.toDoListItem.id + " did mount");
    }
    handleItemUp = () => {
        this.props.moveItemCallBack(this.props.toDoListItem.id,1);
    }
    handleItemDown = () => {
        this.props.moveItemCallBack(this.props.toDoListItem.id,-1);
    }
    handleDeleteItem = () =>{
        this.props.deleteItemCallBack(this.props.toDoListItem.id);
    }

    handleTaskInput = () =>{
        this.setState({
            taskChange:true
        })
    }
    handleTaskChange = (e) =>{
        console.log(e.value);
        this.props.taskChangeCallBack(this.props.toDoListItem.id,e.value);
    }

    render() {
        // DISPLAY WHERE WE ARE
        console.log("\t\t\tToDoItem render");
        let listItem = this.props.toDoListItem;
        let statusType = "status-complete";
        if (listItem.status === "incomplete")
            statusType = "status-incomplete";

        return (
            <div id={'todo-list-item-' + listItem.id} className='list-item-card'>
                {this.state.taskChange ? 
                    <input className='item-col task-col' type="text" value={listItem.description} onBlur={this.hand}></input> 
                  : <div className='item-col task-col' onClick={this.handleTaskInput}>{listItem.description}</div>
                  }
                <div className='item-col due-date-col'>{listItem.due_date}</div>
                <div className='item-col status-col' className={statusType}>{listItem.status}</div>
                <div className='item-col test-4-col'></div>
                <div className='item-col list-controls-col'>
                    <KeyboardArrowUp className='list-item-control todo-button' onClick={this.handleItemUp}/>
                    <KeyboardArrowDown className='list-item-control todo-button' onClick={this.handleItemDown}/>
                    <Close className='list-item-control todo-button' onClick={this.handleDeleteItem}/>
                    <div className='list-item-control'></div>
                <div className='list-item-control'></div>
                </div>
            </div>
        )
    }
}

export default ToDoItem;
