'use strict'

// IMPORT ALL THE THINGS NEEDED FROM OTHER JAVASCRIPT SOURCE FILES
import { jsTPS_Transaction } from "../common/jsTPS.js"

export default class UpdateItem_Transaction extends jsTPS_Transaction {
    constructor(initModel, initItemId, initOldDesc, initNewDesc, initOldDate, initNewDate, initOldStatus, initNewStatus) {
        super();
        this.model = initModel;
        this.itemId = initItemId;
        this.oldDesc = initOldDesc;
        this.newDesc = initNewDesc;
        this.oldDate = initOldDate;
        this.newDate = initNewDate;
        this.oldStatus = initOldStatus;
        this.newStatus = initNewStatus;
    }

    doTransaction() {
        this.model.updateItem(this.itemId, this.newDesc, this.newDate, this.newStatus);
    }

    undoTransaction() {
        this.model.updateItem(this.itemId, this.oldDesc, this.oldDate, this.oldStatus);
    }
}
